sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
		var rightnow= new GlideDateTime();

		var ga = new GlideAggregate("x_58872_needit_needit_task");
		ga.addAggregate('COUNT');
		ga.addQuery("due_date",'<=', rightnow);
	    ga.addQuery('assigned_to', current.assigned_to);
		ga.addQuery("state","!=",3);
		ga.query();
		if (ga.next()) {
					var count= ga.getAggregate('COUNT');
			if(count>4){
			template.print("In addition to the current task,You have "+(count-1) +"tasks with Overdue due date");}
		}


})(current, template, email, email_action, event);
